import React, { Component } from 'react';

class Clock extends Component {
  constructor() {
    super();
    this.state = {
      time: new Date()
    };
  }
  componentDidMount() {
    setInterval(this.update, 30000);
  }
  update = () => {
    this.setState({
      time: new Date()
    });
  };
  render() {
    const day = this.state.time.getDay();
    return (
      <div className="col-4">
        <div className="tile">
          <div>December 4 {day}</div>
          <div class="tile-icon">
            <b>11:11 PM</b>
          </div>
          <div>Wednesday</div>
        </div>
      </div>
    );
  }
}

export default Clock;
