import React, { Component } from 'react';

class Clock extends Component {
  constructor() {
    super();
    this.state = {
      time: new Date()
    };
  }
  componentDidMount() {
    setInterval(this.update, 30000);
  }
  update = () => {
    this.setState({
      time: new Date()
    });
  };
  render() {
    const daysOW = [
      'Sunday',
      'Monday',
      'Tuesday',
      'Wednesday',
      'Thursday',
      'Friday',
      'Saturday'
    ];
    const day = daysOW[this.state.time.getDay()];
    return (
      <div className="col-4">
        <div className="tile">
          <div>December 4</div>
          <div class="tile-icon">
            <b>11:11 PM</b>
          </div>
          <div>{day}</div>
        </div>
      </div>
    );
  }
}

export default Clock;
