import React, { Component } from 'react';
import { Link } from 'react-router-dom';

import axios from 'axios';

class Remote extends Component {
  click = command => {
    const url = 'http://10.66.1.141:8060';
    axios.post(url + command).then(res => {
      console.log('hello!!!');
    });
  };
  render() {
    return (
      <div className="container-fluid">
        <div className="row">
          <div className="col-2">
            <Link to="/">
              <div className="tile">
                <div>Click to</div>
                <div class="tile-icon">
                  <i class="fas fa-home op"></i>
                </div>
                <div>Go Home</div>
              </div>
            </Link>
          </div>
        </div>
        <button onClick={() => this.click('/keypress/home')}>Home</button>
        <button onClick={() => this.click('/keypress/up')}>Up</button>
        <button onClick={() => this.click('/keypress/down')}>Down</button>
        <button onClick={() => this.click('/keypress/left')}>Left</button>
        <button onClick={() => this.click('/keypress/right')}>Right</button>
        <button onClick={() => this.click('/keypress/volumeUp')}>
          Volume Up
        </button>
        <button onClick={() => this.click('/keypress/volumeDown')}>
          Volume Down
        </button>
        <button onClick={() => this.click('/keypress/volumeMute')}>Mute</button>
        <button onClick={() => this.click('/keypress/select')}>OK</button>
        <button onClick={() => this.click('/keypress/play')}>Play</button>
        <button onClick={() => this.click('/keypress/rev')}>rev</button>
        <button onClick={() => this.click('/keypress/fwd')}>fwd</button>
        <button onClick={() => this.click('/keydown/Lit_r')}>R</button>

        <button onClick={() => this.click('/launch/837')}>APP: YouTube</button>
        <button onClick={() => this.click('/launch/13535')}>APP: Plex</button>
        <button onClick={() => this.click('/launch/12')}>APP: Netflix</button>
        <button onClick={() => this.click('/launch/2595')}>
          APP: Crunchyroll
        </button>
      </div>
    );
  }
}

export default Remote;
