import React, { Component } from 'react';
import axios from 'axios';

class Youtube extends Component {
  constructor() {
    super();
    this.state = {
      vid: 'Y7dpJ0oseIA',
      loading: true,
      display: false,
      time: new Date(),
      clockCode: 0
    };
  }

  componentDidMount() {
    setInterval(() => {
      // Clock code
      const addZero = i => {
        if (i < 10) {
          i = '0' + i;
        }
        return i;
      };
      const clockCode =
        addZero(this.state.time.getHours()) +
        '' +
        addZero(this.state.time.getMinutes()) +
        '' +
        addZero(this.state.time.getSeconds());
      this.setState({
        time: new Date(),
        clockCode
      });
      this.displayer(clockCode);
    }, 1000);

    const KEY = 'AIzaSyCedIlWoDmVbm_p9-RPZbx0uSKOIXB-Ack';
    const QUERY = 'J%C3%B3venes';
    const CHANNEL =
      'UCNpffyr-7_zP1x1lS89ByaQ&part=snippet,id&order=date&maxResults=20';
    const url =
      'https://www.googleapis.com/youtube/v3/search?q=' +
      QUERY +
      '&key=' +
      KEY +
      '&channelId=' +
      CHANNEL;
    axios.get(url).then(res => {
      const vid = res.data.items[0].id.videoId;
      console.log(vid);
      this.setState({ vid, loading: false });
    });
  }

  displayer = x => {
    console.log(x);
  };
  render() {
    let videoPlayer;
    if (!this.state.display) {
      videoPlayer = '';
    } else if (this.state.loading) {
      videoPlayer = 'loading..';
    } else {
      videoPlayer = (
        <div className="ytv">
          <iframe
            width="560"
            height="315"
            src={
              'https://www.youtube.com/embed/' + this.state.vid + '?autoplay=1'
            }
            frameBorder="0"
            allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
            allowFullScreen
          ></iframe>
        </div>
      );
    }
    return <div>{videoPlayer}</div>;
  }
}

export default Youtube;
