import React, { Component } from 'react';
import axios from 'axios';

import bellAudio from './sounds/bell.mp3';
import gameOnAudio from './sounds/game_on.mp3';

class Alarm extends Component {
  constructor() {
    super();
    this.state = {
      info: {},
      users: [],
      time: new Date(),
      clockCode: 0,
      alarmGameOn: false,
      alarmBus1: false
    };
  }
  componentDidMount() {
    setInterval(() => {
      this.alarm();
      this.taskMaster();
    }, 1000);
  }
  alarm = () => {
    const url = 'http://10.66.1.128:5000';

    // get users stats
    axios.get(url + '/api/tokens/ssj').then(res => {
      this.setState({ users: res.data, loading: false });
    });

    // get general info / debug info
    axios.get(url + '/api/tokens/info').then(res => {
      this.setState({ info: res.data });
    });

    // Clock code
    const addZero = i => {
      if (i < 10) {
        i = '0' + i;
      }
      return i;
    };
    const clockCode =
      addZero(this.state.time.getHours()) +
      '' +
      addZero(this.state.time.getMinutes()) +
      '' +
      addZero(this.state.time.getSeconds());
    this.setState({
      time: new Date(),
      clockCode
    });

    // Sound control
    this.soundControl(parseInt(this.state.clockCode));
  };

  soundControl = n => {
    console.log(n);

    // GAME ON
    if (n >= 155510 && n <= 155520 && !this.state.alarmGameOn) {
      const audio = new Audio(gameOnAudio);
      audio.play();
      console.log('playing sound');
      this.setState({ alarmGameOn: true });
    }
    // BUS BELL
    else if (n >= 155900 && n <= 155950 && !this.state.alarmBus1) {
      const audio = new Audio(bellAudio);
      audio.play();
      console.log('playing sound');
      this.setState({ alarmBus1: true });
    }

    // DOG FEEDER
  };

  taskMaster = n => {
    console.log('taskmaster');
  };

  render() {
    return <div></div>;
  }
}

export default Alarm;
