import React, { Component } from 'react';
import axios from 'axios';

class Weather extends Component {
  constructor() {
    super();
    this.state = {
      loading: true,
      weather: []
    };
  }
  componentDidMount() {
    this.update();
  }

  update = () => {
    // get weather
    axios
      .get(
        'http://fcc-weather-api.glitch.me/api/current?lat=30.333071&lon=-95.350822'
      )
      .then(res => {
        this.setState({ weather: res.data, loading: false });
      });
  };
  render() {
    if (this.state.loading) {
      return (
        <div className="col-2">
          <div className="tile">
            <div className="bar"></div>
            <div className="text">
              <div>Weather</div>
              <div className="tile-icon">
                <i className="fas fa-cloud op"></i>
              </div>
              <div>{this.state.weather[0].weather.icon}</div>
            </div>
          </div>
        </div>
      );
    }
    return (
      <div className="col-2">
        <div className="tile">
          <div className="bar"></div>
          <div className="text">
            <div>Weather</div>
            <div className="tile-icon">
              <i className="fas fa-cloud op"></i>
            </div>
            <div>{this.state.weather[0].weather.icon}</div>
          </div>
        </div>
      </div>
    );
  }
}

export default Weather;
