import React, { Component } from 'react';

class Sheep extends Component {
  componentDidMount() {
    this.update();
    this.interval = setInterval(this.update, 1000);
  }
  render() {
    let n = 5;
    let lifebar;
    if (n === 0) {
      lifebar = (
        <div className="col-md-12">
          <button className="life"></button>
          <button className="life"></button>
          <button className="life"></button>
          <button className="life"></button>
          <button className="life"></button>
        </div>
      );
    } else if (n === 1) {
      lifebar = (
        <div className="col-md-12">
          <button className="life col-red"></button>
          <button className="life"></button>
          <button className="life"></button>
          <button className="life"></button>
          <button className="life"></button>
        </div>
      );
    } else if (n === 2) {
      lifebar = (
        <div className="col-md-12">
          <button className="life col-orange"></button>
          <button className="life col-orange"></button>
          <button className="life"></button>
          <button className="life"></button>
          <button className="life"></button>
        </div>
      );
    } else if (n === 3) {
      lifebar = (
        <div className="col-md-12">
          <button className="life col-yellow"></button>
          <button className="life col-yellow"></button>
          <button className="life col-yellow"></button>
          <button className="life"></button>
          <button className="life"></button>
        </div>
      );
    } else if (n === 4) {
      lifebar = (
        <div className="col-md-12">
          <button className="life col-green"></button>
          <button className="life col-green"></button>
          <button className="life col-green"></button>
          <button className="life col-green"></button>
          <button className="life"></button>
        </div>
      );
    } else if (n === 5) {
      lifebar = (
        <div className="col-md-12">
          <button className="life col-perfect"></button>
          <button className="life col-perfect"></button>
          <button className="life col-perfect"></button>
          <button className="life col-perfect"></button>
          <button className="life col-perfect"></button>
        </div>
      );
    }
    return (
      <div className="ytv pt-5">
        <div className="container">
          <div className="row">
            <div className="col-md-12 text-center">
              <img
                src="https://media2.giphy.com/media/6h5IYmoBn2xYQ/giphy.gif"
                alt=""
                className="img-fluid"
              />
            </div>
            {lifebar}
          </div>
        </div>
      </div>
    );
  }
}
export default Sheep;
