import React, { Component } from 'react';
import './App.css';

class App extends Component {
  constructor() {
    super();
    this.state = {
      time: new Date(),
      clockCode: 0
    };
  }
  componentDidMount() {
    setInterval(this.update, 1050);
  }
  update = () => {
    // Clock code
    const addZero = i => {
      if (i < 10) {
        i = '0' + i;
      }
      return i;
    };
    const clockCode =
      addZero(this.state.time.getHours()) +
      '' +
      addZero(this.state.time.getMinutes()) +
      '' +
      addZero(this.state.time.getSeconds());
    this.setState({
      time: new Date(),
      clockCode
    });

    // Sound control
    this.soundControl(parseInt(this.state.clockCode));
  };

  soundControl = n => {
    console.log(n);
  };

  render() {
    return <div>{display}</div>;
  }
}

export default App;
