import React, { Component } from 'react';
import axios from 'axios';

class Youtube extends Component {
  constructor() {
    super();
    this.state = {
      vid: 'Y7dpJ0oseIA'
    };
  }

  componentDidMount() {
    const KEY = 'AIzaSyCedIlWoDmVbm_p9-RPZbx0uSKOIXB-Ack';
    const QUERY = 'J%C3%B3venes';
    const CHANNEL =
      'UCNpffyr-7_zP1x1lS89ByaQ&part=snippet,id&order=date&maxResults=20';
    const url =
      'https://www.googleapis.com/youtube/v3/search?q=' +
      QUERY +
      '&key=' +
      KEY +
      '&channelId=' +
      CHANNEL;
    axios.get(url).then(res => {
      console.log(res.data.items[0].id.videoId);
      this.setState({ vid: res.data.items[0].id.videoId });
    });
  }
  render() {
    return (
      <div>
        <iframe
          width="560"
          height="315"
          src={'https://www.youtube.com/embed/' + this.state.vid}
          frameborder="0"
          allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
          allowfullscreen
        ></iframe>
      </div>
    );
  }
}

export default Youtube;
