import React, { Component } from 'react';
import axios from 'axios';

class Taskmaster extends Component {
  constructor() {
    super();
    this.state = {
      info: {}
    };
  }
  componentDidMount() {
    const url = 'https://safe-anchorage-33083.herokuapp.com';

    // get general info / debug info
    axios.get(url + '/api/tokens/info').then(res => {
      this.setState({ info: res.data });
    });
  }
  onClick = n => {
    const url = 'https://safe-anchorage-33083.herokuapp.com';

    // Morning
    if (n === 0) {
      // TASK: Jackeline
      axios
        .post(url + '/api/tokens/task/1', {
          title: 'morning',
          block: 1
        })
        .then(function(res) {
          console.log(res);
        })
        .catch(function(err) {
          console.log(err);
        });
      // TASK: Juan
      axios
        .post(url + '/api/tokens/task/4', {
          title: 'morning',
          block: 1
        })
        .then(function(res) {
          console.log(res);
        })
        .catch(function(err) {
          console.log(err);
        });
      // TASK: Jose
      axios
        .post(url + '/api/tokens/task/3', {
          title: 'morning',
          block: 1
        })
        .then(function(res) {
          console.log(res);
        })
        .catch(function(err) {
          console.log(err);
        });
      console.log('Morning tasks created.');
    } else if (n === 1) {
      //   console.log('yerp');
    }
  };
  render() {
    return (
      <div class="text-center pt-3">
        <div>{this.state.info.colorID}</div>
        <button
          onClick={() => {
            this.onClick(0);
          }}
        >
          <div>
            <i class="fas fa-coffee"></i>
          </div>
          <div>Morning Block</div>
        </button>
      </div>
    );
  }
}

export default Taskmaster;
