import React, { Component } from 'react';
import axios from 'axios';

import { Link } from 'react-router-dom';

import Tile from './tiles/Tile';
import SolidTile from './tiles/SolidTile';
import Clock from './tiles/Clock';
import Emojis from './tiles/Emojis';
import Playlist from './tiles/Playlist';
import Habitica from './habitica/HabiticaTile';
class Home extends Component {
  constructor() {
    super();
    this.state = {
      info: {},
      users: [],
      loading: true,
      catBus: []
    };
  }

  componentDidMount() {
    this.update();
    this.interval = setInterval(this.update, 7500);
  }
  componentWillUnmount() {
    clearInterval(this.interval);
  }
  update = () => {
    const url = 'http://10.66.1.128:5000';
    // get users stats
    axios.get(url + '/api/tokens/ssj').then(res => {
      this.setState({ users: res.data, loading: false });
    });

    // get general info / debug info
    axios.get(url + '/api/tokens/info').then(res => {
      this.setState({ info: res.data });
    });

    // get bus info
    axios.get(url + '/api/tokens/cat/7').then(res => {
      this.setState({ catBus: res.data });
      console.log(this.state.catBus);
    });

    console.log(Date.now() + ': updating');
  };

  render() {
    // emoji
    const loading = this.state.loading;
    let emoji;
    if (loading) {
      emoji = 'loading';
    } else {
      emoji = <Emojis info={this.state.users} />;
    }

    // chores blocks 0: morning, 1: afternoon, 2: night, 3: sleep
    const block = this.state.info.blockID;
    let tasker;

    if (block === 1) {
      tasker = (
        <Tile
          top="Chores"
          bot="0/5 Completed"
          icon="fas fa-broom op"
          to="/chores"
          progress=""
        />
      );
    } else if (block === 2) {
      tasker = (
        <Tile
          top="Night Routine"
          bot="Before Play Checklist"
          icon="fas fa-praying-hands op"
          to="/chores"
          progress=""
        />
      );
    } else if (block === 3) {
      tasker = (
        <Tile
          top="Good Night"
          bot="Sleep Time"
          icon="fas fa-moon op"
          to="/chores"
          progress=""
        />
      );
    } else {
      tasker = (
        <Tile
          top="Chores"
          bot="Morning Check-In"
          icon="fas fa-broom op"
          to="/chores"
          progress=""
        />
      );
    }

    return (
      <div className="container-fluid">
        <div className="row">
          <Habitica />
          <Tile
            top="Roku Remote"
            bot="controller"
            icon="fas fa-mobile op"
            to="/remote"
          />
          <Tile
            top="TV Lights"
            bot="disabled"
            icon="fas fa-lightbulb op"
            to="/"
          />
          <Tile
            top="Living Room LED"
            bot="disabled"
            icon="fas fa-lightbulb op"
            to="/"
          />
          <Clock />
          {tasker}
          <Tile
            top="Shower"
            bot="Complete"
            icon="fas fa-bath op"
            to="/"
            progress="b-75"
          />
          <SolidTile
            data={this.state.catBus}
            top="Bus Pickup"
            icon="fas fa-bus"
            to="/"
          />
          <SolidTile
            data={this.state.catBus}
            top="Feed Dog"
            icon="fas fa-hamburger"
            to="/"
          />
          <SolidTile
            data={this.state.catBus}
            top="Walk dog"
            icon="fas fa-baby-carriage"
            to="/"
          />
          <SolidTile
            data={this.state.catBus}
            top="Trashbin"
            icon="fas fa-trash"
            to="/"
          />

          {emoji}
          <Playlist />
        </div>
      </div>
    );
  }
}

export default Home;
