import React, { Component } from 'react';
import axios from 'axios';

import { Link } from 'react-router-dom';

import Tile from './tiles/Tile';
import Clock from './tiles/Clock';
import Emojis from './tiles/Emojis';
import Playlist from './tiles/Playlist';
import Habitica from './habitica/HabiticaTile';
class Home extends Component {
  constructor() {
    super();
    this.state = {
      users: [],
      loading: true
    };
  }

  componentDidMount() {
    setInterval(this.update, 500);
  }
  update = () => {
    console.log('hello word');
    const url = 'http://localhost:5000';
    let users = [];
    axios
      .get(url + '/api/tokens/ssj')
      .then(res => {
        this.setState({ users: res.data, loading: false });
      })
      .then(() => {
        console.log(this.state.loading);
      });
  };

  render() {
    return (
      <div className="container-fluid">
        <div className="row">
          <Habitica />
          <Tile
            top="Roku Remote"
            bot="controller"
            icon="fas fa-mobile op"
            to="/remote"
          />
          <Tile
            top="TV Lights"
            bot="disabled"
            icon="fas fa-lightbulb op"
            to="/"
          />
          <Tile
            top="Living Room LED"
            bot="disabled"
            icon="fas fa-lightbulb op"
            to="/"
          />
          <Clock />
          <Tile
            top="Chores"
            bot="Morning Check-In"
            icon="fas fa-broom op"
            to="/chores"
            progress="b-100"
          />
          <Tile
            top="Shower"
            bot="Complete"
            icon="fas fa-bath op"
            to="/"
            progress="b-75"
          />
          <Tile
            top="Bus Pickup"
            bot="Up next: Andrea"
            icon="fas fa-bus op"
            to="/"
            progress=""
          />
          <Tile
            top="Feed Dog"
            bot="Up next: 6PM"
            icon="fas fa-hamburger op"
            to="/"
            progress=""
          />
          <Tile
            top="Walk dog"
            bot="Complete"
            icon="fas fa-baby-carriage op"
            to="/"
            progress=""
          />
          <Tile
            top="Trashbin"
            bot="Thursday night"
            icon="fas fa-trash op"
            to="/"
            progress=""
          />

          {/* <Emojis info={this.state.users} /> */}

          <Playlist />
        </div>
      </div>
    );
  }
}

export default Home;
