import React, { Component } from 'react';
import { Link, useParams } from 'react-router-dom';
import axios from 'axios';
import Tile from '../tiles/Tile';

class Modal extends Component {
  constructor() {
    super();
    this.state = {
      data: []
    };
  }
  componentDidMount() {
    const url = 'http://10.66.1.128:5000';

    console.log(this.props.match.params.id);
    const code = this.props.match.params.id;
    // get bus info
    axios.get(url + '/api/tokens/cat/' + code).then(res => {
      this.setState({ data: res.data });
      console.log(this.state.data);
    });
  }

  render() {
    const tasker = this.state.data;
    const users = [
      'Andrea',
      'Jackeline',
      'Alfredo',
      'Manuel',
      'Juan',
      'Anyone'
    ];
    const tasks = tasker.map(task => (
      <div className="col-sm-4 p-1">
        <div className="box text-center">
          <h2>{users[task.userID]}</h2>
          <button className="btn btn-info" hmm="{task.userID}/{task._id}">
            Mark as complete.
          </button>
        </div>
      </div>
    ));
    return (
      <div>
        <Tile top="Home" bot="" icon="fas fa-home op" to="/" progress="" />
        <div className="row pl-3 pr-1">{tasks}</div>
      </div>
    );
  }
}

export default Modal;
