import React, { Component } from 'react';
import axios from 'axios';

import bellAudio from './sounds/bell.mp3';
import busAudio from './sounds/bus.mp3';
import gameOnAudio from './sounds/game_on.mp3';
import strawberryAudio from './sounds/strawberry.mp3';

class Alarm extends Component {
  constructor() {
    super();
    this.state = {
      info: {},
      users: [],
      time: new Date(),
      clockCode: 0,
      alarmGameOn: false,
      alarmBus1: false,
      catFeed: [],
      catWalk: [],
      catBus: [],
      catTrash: [],
      buffer: 0
    };
  }
  componentDidMount() {
    this.fetch();

    setInterval(() => {
      this.alarm();
      this.taskMaster();
    }, 1000);
  }

  fetch = () => {
    const url = 'http://10.66.1.128:5000';

    // get users stats
    axios.get(url + '/api/tokens/ssj').then(res => {
      this.setState({ users: res.data, loading: false });
    });

    // get general info / debug info
    axios.get(url + '/api/tokens/info').then(res => {
      this.setState({ info: res.data });
    });

    // get bus info
    axios.get(url + '/api/tokens/cat/7/true').then(res => {
      this.setState({ catBus: res.data });
    });

    // get dog feed info
    axios.get(url + '/api/tokens/cat/4/true').then(res => {
      this.setState({ catFeed: res.data });
    });

    // get dog walk info
    axios.get(url + '/api/tokens/cat/5/true').then(res => {
      this.setState({ catWalk: res.data });
    });

    // get trash info
    axios.get(url + '/api/tokens/cat/6/true').then(res => {
      this.setState({ catTrash: res.data });
    });
  };
  alarm = () => {
    this.fetch();

    // Clock code
    const addZero = i => {
      if (i < 10) {
        i = '0' + i;
      }
      return i;
    };
    const clockCode =
      addZero(this.state.time.getHours()) +
      '' +
      addZero(this.state.time.getMinutes()) +
      '' +
      addZero(this.state.time.getSeconds());
    this.setState({
      time: new Date(),
      clockCode
    });

    // Sound control
    this.soundControl(parseInt(this.state.clockCode));
  };

  soundControl = n => {
    let buffer = false;
    if (15000 < Date.now() - this.state.buffer) {
      buffer = true;
    }

    console.log(this.state.info.colorID);

    // GAME ON
    if (n >= 164300 && n <= 164350 && !this.state.alarmGameOn) {
      const audio = new Audio(gameOnAudio);
      audio.play();
      console.log('playing sound');
      this.setState({ alarmGameOn: true });
      this.setState({ buffer: Date.now() });
    }
    // BUS BELL
    else if (n >= 155900 && n <= 155950 && !this.state.alarmBus1) {
      const audio = new Audio(bellAudio);
      audio.play();
      console.log('playing sound');
      this.setState({ alarmBus1: true });
    }
    // BUS
    else if (this.state.catBus.length > 0 && buffer) {
      const audio = new Audio(busAudio);
      audio.play();
      console.log('playing sound');
      this.setState({ buffer: Date.now() });
    }
    // TRASH TAKER
    else if (this.state.catTrash.length > 0 && buffer) {
      const audio = new Audio(strawberryAudio);
      audio.play();
      console.log('playing sound');
      this.setState({ buffer: Date.now() });
    }
    // DOG FEEDER
    else if (this.state.catFeed.length > 0 && buffer) {
      const audio = new Audio(strawberryAudio);
      audio.play();
      console.log('playing sound');
      this.setState({ buffer: Date.now() });
    }
    // DOG WALKER
    else if (this.state.catWalk.length > 0 && buffer) {
      const audio = new Audio(gameOnAudio);
      audio.play();
      console.log('playing sound');
      this.setState({ buffer: Date.now() });
    }
  };

  taskMaster = n => {
    console.log('taskmaster');
  };

  render() {
    return <div></div>;
  }
}

export default Alarm;
