import React, { Component } from 'react';

import bellAudio from './sounds/bell.mp3';
import gameOnAudio from './sounds/game_on.mp3';

class Alarm extends Component {
  constructor() {
    super();
    this.state = {
      time: new Date(),
      clockCode: 0,
      alarmGameOn: false
    };
  }
  componentDidMount() {
    setInterval(() => {
      this.alarm();
      this.taskMaster();
    }, 1000);
  }
  alarm = () => {
    // Clock code
    const addZero = i => {
      if (i < 10) {
        i = '0' + i;
      }
      return i;
    };
    const clockCode =
      addZero(this.state.time.getHours()) +
      '' +
      addZero(this.state.time.getMinutes()) +
      '' +
      addZero(this.state.time.getSeconds());
    this.setState({
      time: new Date(),
      clockCode
    });

    // Sound control
    this.soundControl(parseInt(this.state.clockCode));
  };

  soundControl = n => {
    console.log(n);

    // first test
    if (n >= 155410 && n <= 155420 && !this.state.alarmGameOn) {
      const audio = new Audio(gameOnAudio);
      audio.play();
      console.log('play sound');
      this.setState({ alarmGameOn: true });
    }
    if (n === 155025) {
      const audio = new Audio(bellAudio);
      audio.play();
      console.log('play sound');
    }
  };

  taskMaster = n => {
    console.log('taskmaster');
  };

  render() {
    return <div></div>;
  }
}

export default Alarm;
