import React, { Component } from 'react';
import axios from 'axios';

class Taskmaster extends Component {
  constructor() {
    super();
    this.state = {
      info: {}
    };
  }
  componentDidMount() {
    const url = 'https://safe-anchorage-33083.herokuapp.com';

    // get general info / debug info
    axios.get(url + '/api/tokens/info').then(res => {
      this.setState({ info: res.data });
    });
  }
  onClick = n => {
    // Morning
    if (n === 0) {
      this.giveTaskTrio(1);
      this.giveTaskDog(this.state.info.colorID);
      this.giveTaskTrash(10, this.state.info.colorID);
      console.log('Morning tasks created.');
    }
    // Evening
    else if (n === 1) {
      console.log('Chores tasks created.');
    }
  };

  giveTaskTrash = (type, color) => {
    const url = 'https://safe-anchorage-33083.herokuapp.com';
    const names = ['4', '2', '1', '3'];
    const n = names[color];
    axios
      .post(url + '/api/tokens/task/' + n, {
        title: 'trash',
        block: type
      })
      .then(function(res) {
        console.log(res);
      })
      .catch(function(err) {
        console.log(err);
      });
  };

  giveTaskDog = n => {
    const url = 'https://safe-anchorage-33083.herokuapp.com';

    if (n === 0 || n === 2) {
      console.log(n + '?');
      axios
        .post(url + '/api/tokens/task/4', {
          title: 'feed dog',
          block: 4
        })
        .then(function(res) {
          console.log(res);
        })
        .catch(function(err) {
          console.log(err);
        });
      axios
        .post(url + '/api/tokens/task/3', {
          title: 'walk dog',
          block: 5
        })
        .then(function(res) {
          console.log(res);
        })
        .catch(function(err) {
          console.log(err);
        });
    } else if (n === 1 || n === 3) {
      axios
        .post(url + '/api/tokens/task/4', {
          title: 'walk dog',
          block: 5
        })
        .then(function(res) {
          console.log(res);
        })
        .catch(function(err) {
          console.log(err);
        });
      axios
        .post(url + '/api/tokens/task/3', {
          title: 'feed dog',
          block: 4
        })
        .then(function(res) {
          console.log(res);
        })
        .catch(function(err) {
          console.log(err);
        });
    }
  };

  giveTaskTrio = n => {
    const url = 'https://safe-anchorage-33083.herokuapp.com';

    // TASK: Jackeline
    axios
      .post(url + '/api/tokens/task/1', {
        title: 'morning',
        block: n
      })
      .then(function(res) {
        console.log(res);
      })
      .catch(function(err) {
        console.log(err);
      });
    // TASK: Juan
    axios
      .post(url + '/api/tokens/task/4', {
        title: 'morning',
        block: n
      })
      .then(function(res) {
        console.log(res);
      })
      .catch(function(err) {
        console.log(err);
      });
    // TASK: Jose
    axios
      .post(url + '/api/tokens/task/3', {
        title: 'morning',
        block: n
      })
      .then(function(res) {
        console.log(res);
      })
      .catch(function(err) {
        console.log(err);
      });
  };
  render() {
    return (
      <div className="text-center pt-3">
        {/* <div>{this.state.info.colorID}</div> */}
        <button
          className="btn-tm"
          onClick={() => {
            this.onClick(0);
          }}
        >
          <div>
            <i className="fas fa-coffee"></i>
          </div>
          <div>Morning Block</div>
        </button>
        <button
          className="btn-tm"
          onClick={() => {
            this.onClick(1);
          }}
        >
          <div>
            <i class="fas fa-broom"></i>
          </div>
          <div>Chores Block</div>
        </button>
      </div>
    );
  }
}

export default Taskmaster;
